		let pt = points[i];
		push();
		translate(pt.x, pt.y);
		rotate(radians(sin(frameCount * i * 0.00003) * 180));
		let w = dist(mouseX, mouseY, pt.x + width / 4, pt.y + height * .7) / 3;
		line(0, 0, w, w);
		pop();
	}
