/* OSC COMMUNICATION */
function receiveOsc(address, value) {
	// debug received message
	// print('in: ' + address + ', ' +value)
	if(debugIn) {
		debugIn.value(address + ', ' + value)
	}

	// visualize message value
	let x = map(address.substring(1), 0, 9, 0, width);
	ellipse(x, height / 3, width / 10 * value);
}

function sendOsc(address, value) {
	if(socket != undefined) {
		socket.emit('message', [address].concat(value));
	}

	// debug sent message
	if(debugOut) {
		debugOut.value(address + ', ' + value)
	}
}

function setupOsc(oscHost, oscPortIn, oscPortOut) {
	socket = io.connect('http://127.0.0.1:8082');
	socket.on('connect', function() {
		socket.emit('config', {
			server: {
				host: oscHost,
				port: oscPortIn
			},
			client: {
				host: oscHost,
				port: oscPortOut
			}
		});
		print('OSC Ready!\n' + oscHost + ', listen: ' + oscPortIn + ', send: ' + oscPortOut);
	});
	socket.on('message', function(msg) {
		receiveOsc(msg[0], msg.splice(1));
	});
}

