/*	aboutsketch // cc teddavis.org 2019	*/

function setup() {
	createCanvas(windowWidth, windowHeight);
	noStroke();
	mouseX = width/2;
	mouseY = height/2;
}

function draw() {
	background(0);
	tri(0, 0, mouseX, mouseY, 0, height, 100);
	tri(0, 0, mouseX, mouseY, width, 0, 200);
	tri(width, height, mouseX, mouseY, 0, height, 0);
	tri(width, height, mouseX, mouseY, width, 0, 255);
}

function tri(x1, y1, x2, y2, x3, y3, fc){
	var v = 0.002;
	fill(abs(sin(fc+(frameCount*v)))*255);
	beginShape();
	vertex(x1, y1);
	vertex(x2, y2);
	vertex(x3, y3);
	endShape();
}

/*	bwEllipse // cc teddavis.org 2019	*/

function setup() { 
	createCanvas(windowWidth, windowHeight);
	pixelDensity(1);
}

function draw() {
	// background(0)
	fill(255);
	stroke(255);
	if(frameCount%2==0)
	stroke(0);
	strokeWeight(15);
	var oscX = sin(frameCount*0.01)*width*.4;
	var oscY = sin(frameCount*0.045)*height*.4;
	var s = sin(frameCount*0.02)*150;
	translate(width/2, height/2);
	ellipse(oscX, oscY, s, s);
}

/*	minSyntax // cc teddavis.org 2019	*/

var y=0,x=0,s=0,v=0,h=0

function setup() {
	createCanvas(windowWidth, windowHeight)
	genRandom()
}

function draw() {
	x += v
	if(x > width+s){
	    x = -s
	}

	if(x < -s/2){
	    genRandom()
	}
	fill(255)
	ellipse(x, y+sin(frameCount/10)*h, s)
}

function genRandom(){
    y = random(height)
	s = random(20, 200)
    v = s/20
    h = random(v, v*10)
}

/*	easeFunction // cc teddavis.org 2019	*/

var x=0, y=0, e=0.05;

function setup() {
	createCanvas(windowWidth, windowHeight);
	fill(255);
}

function draw() {
	background(200);

	// built-in ease(inVal, outVal, easeVal) for smoothing values
	x = ease(mouseX, x, e);
	y = ease(mouseY, y, e);

	ellipse(x, y, 150, 150);
	text(nf(x, 3,1) + " / " + nf(y, 3,1), 0, height);

}

/*	sinStrings // cc teddavis.org 2019	*/

function setup() {
	createCanvas(windowWidth, windowHeight);
	mouseX = width * 0.4;
	pixelDensity(1);
}

function draw() {
	background(200);
	var lc = 40;
	fill(0);
	for(var i = 0; i < lc; i++) {
		var s = width / lc;
		var x = map(i, 0, lc - 1, 0, width);
		var osc = sin((frameCount * .01) + i * map(mouseX, 0, width, 0, 5));
		var y = height / 2 + osc * abs(mouseY - height / 2);
		stroke(150);
		strokeWeight(2.1 - (osc * 2));
		line(x, y, x, 0);
		noStroke();
		ellipse(x, y, s, s);
	}
}

/*	wanderingWorm // cc teddavis.org 2019	*/

var s = 50.0;
var sMin = 25;
var sg = 2;
var x = 0.0;
var y = 0.0;
var nx = 0.0;
var ny = 0.0;
var nxv = 0.006;
var nyv = 0.005;

function setup() {
	createCanvas(windowWidth, windowHeight);
	noCursor();
	nx = random(width);
	ny = random(height);
	background(20);
	pixelDensity(1);
}

function draw() {
	x = floor(noise(nx)*width);
	y = floor(noise(ny)*height);
	fill(255);
	if(mouseIsPressed){
		s += sg;
	}else{
		s -= sg;
		if(s < sMin)
			s = sMin;
	}

	ellipse(x, y, s, s);

	nx += nxv;
	ny += nyv;
}

function keyPressed(){
	if(keyCode == 8)
		background(0);
}

/*	typeScale // cc teddavis.org 2019	*/

function setup() {
  createCanvas(windowWidth, windowHeight);
}

function draw() {
  background(0);
  fill(255);
  stroke(255);
  textSize(mouseX);
  var myText = "P5LIVE";
  textFont("Monaco");
  text(myText, 0, mouseY);
  var tw = textWidth(myText);
  var ta = textAscent()/2;
  var lc = 10;
  for(var i=0; i < lc; i++){
    var y = map(i, 0, lc-1, 0, mouseY-ta);
    var y2 = map(i, 0, lc-1, height, mouseY);
    stroke(255);
    line(0, y, width, y);
    line(0, y2, width, y2);
    line(tw, 0, tw, height);
    stroke(255, 0, 0);
    line(mouseX, 0, mouseX, height);
    line(0, mouseY, width, mouseY);
  }
}

