var myText = "hello world";
var shifted = false;
var b = 15;

function setup() {
  createCanvas(windowWidth, windowHeight);
  background(0);
}

function draw() {
  fill(0, 20);
  noStroke();
  rect(0, 0, width, height);
  fill(255, 80);
  //stroke(255);
  textFont("Monaco");
  textSize(100);
  var rv = 2;
  var rx = random(-rv, rv);
  var ry = random(-rv, rv);
  text(myText, b+rx, b+ry, width-b*2, height-b*2);

}

function keyPressed(){
    if(keyCode == 8){
        if(shifted){
            myText = "";
            background(0);
        }else{
            myText = myText.substring(0, myText.length-1);
            background(0);
        }
    }else if(keyCode == 16){
        shifted = true;
    }else if(keyCode != 18 && keyCode != 91 && keyCode != 93 && keyCode != 17  && keyCode != 16 && keyCode != 17){
        if(key == "Enter")
        	key = "\n";
        myText += key + "";
    }
}

function keyReleased(){
    if(keyCode == 16){
        shifted = false;
    }
}

