/*	webgl_sphereBox // cc teddavis.org 2019	*/

var lc = 8;

function setup() {
	createCanvas(windowWidth, windowHeight, WEBGL);
	noCursor();
}

function draw() {
	ortho();
	background(0);
	for(var i = 0; i < lc; i++) {
		for(var j = 0; j < lc; j++) {
			var mx = map(i, 0, lc - 1, -width / 2, width / 2);
			var my = map(j, 0, lc - 1, -height / 2, height / 2);

			boxsphere(mx, my);
		}
	}
}

function boxsphere(x, y) {
	push();
	translate(x, y);
	var mmx = map(mouseX, 0, width, -width / 2, width / 2);
	var mmy = map(mouseY, 0, height, -height / 2, height / 2);
	var roff = -dist(mmx, mmy, x, y) / 2;
	rotateY(radians(frameCount + mouseX));
	rotateX(radians(mouseY));
	noFill();
	strokeWeight(2);
	fill(255);
	stroke(255);
	if(mouseIsPressed) {
		fill(0);
		stroke(0);
	}
	var s = (width / lc / 2) + roff;
	box(s);
	fill(0);
	stroke(0);
	if(mouseIsPressed) {
		fill(255);
		stroke(255);
	}
	sphere(s * 0.65);
	pop();
}

