/*	sinStrings // cc teddavis.org 2019	*/

function setup() {
	createCanvas(windowWidth, windowHeight);
	mouseX = width * 0.4;
}

function draw() {
	background(200);
	var lc = 20;
	fill(0);
	for(var i = 0; i < lc; i++) {
		var s = width / lc;
		var x = map(i, 0, lc - 1, 0, width);
		var osc = sin((frameCount * .01) + i * map(mouseX, 0, width, 0, 5));
		var y = height / 2 + osc * abs(mouseY - height / 2);
		stroke(150);
		strokeWeight(2.1 - (osc * 2));
		line(x, y, x, 0);
		noStroke();
		ellipse(x, y, s, s);
	}
}

/*	wanderingWorm // cc teddavis.org 2019	*/

var x, y, nx, ny;
var s = 50.0;
var sMin = 25;
var sg = 2;
var nxv = 0.006;
var nyv = 0.005;

function setup() {
	createCanvas(windowWidth, windowHeight);
	noCursor();
	nx = random(width);
	ny = random(height);
	background(0);
}

function draw() {
	x = floor(noise(nx) * width);
	y = floor(noise(ny) * height);
	fill(255);
	if(mouseIsPressed) {
		s += sg;
	} else {
		s -= sg;
		if(s < sMin)
			s = sMin;
	}

	ellipse(x, y, s, s);

	nx += nxv;
	ny += nyv;
}

function keyPressed() {
	if(keyCode == 8)
		background(0);
}

