	audioAnalysis // cc teddavis.org 2019
	add snippet to other sketches: CTRL + SHIFT + A 
*/

function setup() {
	createCanvas(windowWidth, windowHeight);

	setupAudio();
}

function draw() {
	/* audio vars: amp, ampEase, fft, waveform */
	updateAudio();
	background(0);
	noFill();
	stroke(255);

	/* average */
	ellipse(width/2, height/4, amp);

	/* waveform */
	beginShape();
	for(let i = 0; i < waveform.length; i++) {
		let freq = waveform[i] * height/4; // (-1, 1);
		let x = map(i, 0, waveform.length, 0, width);
		curveVertex(x, height/2+freq);
	}
	endShape();


	/* fft */
	for(let i = 0; i < fft.length; i++) {
		let freq = fft[i]; // (0, 255);
		let x = map(i, 0, fft.length, 0, width);
		let w = width/fft.length;
		rect(x, height, w, -freq);
	}

}

/* AUDIO INIT */
let mic, fftRaw, fft, waveform, amp = 0.0,
	ampEase = 0.0,
	numBins = 512,
	bands = 12; 

function setupAudio() {
	mic = new p5.AudioIn();
	mic.start();
	fftRaw = new p5.FFT(0.75, numBins);
	fftRaw.setInput(mic);
}

function updateAudio() {
	fftRaw.analyze(); 
	amp = mic.getLevel() * 1000; // average amplitude
	ampEase = ease(amp, ampEase, 0.075); // smooth 'amp'
	waveform = fftRaw.waveform(); // array (-1, 1)
	fft = fftRaw.logAverages(fftRaw.getOctaveBands(bands)); // array (0, 255)
}

/*	webgl_sphereBox // cc teddavis.org 2019	*/

var lc = 8;

function setup() {
	createCanvas(windowWidth, windowHeight, WEBGL);
	noCursor();
	noStroke();

}

function draw() {
	ortho();
	background(0);
	for(var i = 0; i < lc; i++) {
		for(var j = 0; j < lc; j++) {
			var mx = map(i, 0, lc - 1, -width / 2, width / 2);
			var my = map(j, 0, lc - 1, -height / 2, height / 2);

			boxsphere(mx, my);
		}
	}
}

function boxsphere(x, y) {
	push();
	translate(x, y);
	var mmx = map(mouseX, 0, width, -width / 2, width / 2);
	var mmy = map(mouseY, 0, height, -height / 2, height / 2);
	var roff = -dist(mmx, mmy, x, y) / 2;
	rotateY(radians(frameCount + mouseX));
	rotateX(radians(mouseY));
	var s = (width / lc / 2) + roff;

	fill(255);
	if(mouseIsPressed) {
		fill(0);
	}
	box(s);

	fill(0);
	if(mouseIsPressed) {
		fill(255);
	}
	sphere(s * 0.65);
	pop();
}

