function setup() {
	createCanvas(windowWidth, windowHeight);
	stroke(255);
	font = loadFont('includes/fonts/RobotoMono-Regular.ttf', getPoints);
	//getPoints();
	rectMode(CENTER);
}

function draw() {
	background(0);
	translate(width / 4, height * .7);
	for(var i = 0; i < points.length; i++) {
		var pt = points[i];
		push();
		translate(pt.x, pt.y);
		rotate(radians(sin(frameCount * i * 0.00003) * 180));
		var w = dist(mouseX, mouseY, pt.x + width / 4, pt.y + height * .7) / 3;
		line(0, 0, w, w);
		pop();
	}
}

function getPoints() {
	var s = width / myText.length;
	points = font.textToPoints(myText, 0, 0, s);
	textFont(font);

}

