code.global:
var mic, wave, fft, amp = 0, ampEase = 0;
var numSamples = 1024;
var numBands = 16;

code.setup:
mic = new p5.AudioIn();
mic.start();
wave = new p5.FFT(0.5, numSamples);
fft = new p5.FFT(0.75, numBands);
wave.setInput(mic);
fft.setInput(mic);

