/* AUDIO INIT */
let mic, fftRaw, fft, waveform, amp = 0.0,
ampEase = 0.0,
numBins = 512,
bands = 12; 

function setupAudio() {
mic = new p5.AudioIn();
mic.start();
fftRaw = new p5.FFT(0.75, numBins);
fftRaw.setInput(mic);
}

function updateAudio() {
fftRaw.analyze(); 
amp = mic.getLevel() * 1000; // average amplitude
ampEase = ease(amp, ampEase, 0.075); // smooth 'amp'
waveform = fftRaw.waveform(); // array (-1, 1)
fft = fftRaw.logAverages(fftRaw.getOctaveBands(bands)); // array (0, 255)
}

// OSC LOGIC 
function receiveOsc(address, value) {
	print("received OSC: " + address + ", " + value);
}
function sendOsc(address, value) {
socket.emit("message", [address].concat(value));
}

function setupOsc(oscPortIn, oscPortOut) {
socket = io.connect("http://127.0.0.1:8082", {
port: 8082,
rememberTransport: false
});
socket.on("connect", function() {
socket.emit("config", {
server: {
port: oscPortIn,
host: "127.0.0.1"
},
client: {
port: oscPortOut,
host: "127.0.0.1"
}
});
});
socket.on("message", function(msg) {
if(msg[0] == "#bundle") {
for(var i = 2; i < msg.length; i++) {
receiveOsc(msg[i][0], msg[i].splice(1));
}
} else {
receiveOsc(msg[0], msg.splice(1));
}
});
}
